import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { borderRadius, maxHeight } from '@material-ui/system';
import oneYearNomad from '../Assets/oneyearnomad.jpg';
import {Link} from 'react-router-dom';
import Modal from '@material-ui/core/Modal';

function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    /*top: '20%' `${top}%`,*/
    /*left: '55%' `${left}%`,*/
    /*transform: `translate(-${top}%, -${left}%)`,*/
    backgroundColor: "white",
    borderRadius: "10px",
    maxWidth: '100%',
    margin: '10% 5% 10% 5%'


  };
}

const imageStyle = {
  maxWidth: '100%'
}

const cardTitle = {
  fontFamily: 'montserrat',
  fontWeight: '600',
  fontSize: '24px',
}

const useStyles = makeStyles({
  card: {
    maxWidth: 345,
    margin: '7%',
    borderRadius: '10px',
    maxHeight: 250


  },
  media: {
    height: 140,


  },
  paper: {
    position: 'absolute',
    maxWidth: '100%',
    backgroundColor: 'grey',
    padding: '10px 10px 10px 10px',





    outline: 'none',
  },
});



export default function MediaCardModal() {

  const classes = useStyles();

  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
    <Card className={classes.card}>
      <CardActionArea onClick={handleOpen}>
        <CardMedia
          className={classes.media}
          image= {oneYearNomad}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2" style={{fontFamily: 'montserrat', fontWeight: '600',}}>
            The One Year Nomad
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p"  >
            Web Development and Social Media

          </Typography>
        </CardContent>
      </CardActionArea>
      </Card>
      <Modal
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
        open={open}
        onClose={handleClose}
      >


        <div style={modalStyle} className={classes.paper}>
        <button>Close</button>
        <img src={oneYearNomad} alt="oneyearnomad" style={imageStyle}></img>
          <h2 id="modal-title" style={cardTitle}>The One Year Nomad</h2>
          <p id="simple-modal-description">
            Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
          </p>

        </div> 
      </Modal>
      </div>

  );
}
