import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import AboutPic from '../Assets/Albert_portfolio.jpg'
import Modal from '@material-ui/core/Modal';
import Clear from '@material-ui/icons/Clear';
import './AboutCard.css';



export default class AboutCards extends React.Component{
    constructor(props) {
      super(props);
      this.state = {
        open: false,
        idmodal: 0
      };
      this.handleOpen = this.handleOpen.bind(this);
      this.handleClose = this.handleClose.bind(this);
    }

    handleOpen(e){
      this.setState(({
        open: true,
      }));
    }

    handleClose(){
      this.setState(({
        open: false
      }));
    }

    render(){

        return(
          <div>

            <Card className="cardAbout" >
                <CardActionArea  onClick={this.handleOpen} style={{height: "290px"}}> 
                  <CardMedia
                    className="mediaAbout"
                    image={AboutPic}
                    title="Contemplative Reptile"
                  />
                  <CardContent style={{marginTop: '-80%'}}>
                    <Typography gutterBottom variant="h4" component="h2" style={{fontFamily: 'montserrat', fontWeight: '600', letterSpacing: '-0.05em',}}>
                    Hello,
                  </Typography>
                    <Typography variant="h5" color="textSecondary" component="p" className="subTitle" style={{fontFamily: 'montserrat', letterSpacing: '-0.02em', maxWidth: "60%", marginTop: "-3%"}}>
                      My name is Albert Collell
                    </Typography>

                  </CardContent>
                </CardActionArea>
              </Card> 



              <Modal
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
              open={this.state.open}
              onClose={this.handleClose}
              >
            <div className="modalStyle">
                 <img src={this.handleImagePath(CardsData[this.state.idmodal].id)} alt="oneyearnomad" className="imageStyle"></img>

                <Clear className="mdc-icon-button material-icons" onClick={this.handleClose} style={{position: 'absolute', top: '10px', right: '10px', size: '36dp'}}>CLOSE</Clear>

                <div style= {{padding: '2% 10% 10% 10%', fontFamily: 'montserrat'}}>
                  <p style={{fontStyle: 'italic', fontSize: '14px', fontWeight: "300"}}>{CardsData[this.state.idmodal].subtitle}</p>
                  <h1 id="modal-title" style={{fontFamily: 'montserrat', fontWeight: '600', fontSize: '24px', letterSpacing: '-0.05em'}}>{CardsData[this.state.idmodal].title}</h1>

                  <Button variant="contained" color="primary"> VISIT </Button>

                  <div style= {{letterSpacing: '-0.02em', fontWeight: '300', paddingBottom: '10%'}}>
                    <p id="simple-modal-description"> 
                    {CardsData[this.state.idmodal].description}
                    </p>
                  <h3 style={{fontWeight: "400"}}>{CardsData[this.state.idmodal].list1Title}</h3>

                  {CardsData[this.state.idmodal].list1.map((i)=> {return (<li>{i}</li>)})}  

                  <h3 style={{fontWeight: "400"}}>{CardsData[this.state.idmodal].list2Title}</h3>

                  {CardsData[this.state.idmodal].list2.map((i)=> {return (<li>{i}</li>)})}

                </div>
                  <br></br>

                  <img src={oneyearnomadMobile} alt="oneyearnomadMobile" style={{maxWidth: '100%', maxHeight: '100%', borderRadius: '10px', boxShadow: '0 2px 2px 0 rgba(0,0,0,0.20)'}}></img>
              </div> 
              </div> 
            </Modal>



        </div>

        );
      }
      }
