import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import AboutPic from '../Assets/Albert_portfolio.jpg'
import Modal from '@material-ui/core/Modal';
import Clear from '@material-ui/icons/Clear';
import './AboutCard.css';



export default class AboutCards extends React.Component{
    constructor(props) {
      super(props);
      this.state = {
        open: false,
        idmodal: 0
      };
      this.handleOpen = this.handleOpen.bind(this);
      this.handleClose = this.handleClose.bind(this);
    }

    handleOpen(e){
      this.setState(({
        open: true,
      }));
    }

    handleClose(){
      this.setState(({
        open: false
      }));
    }

    render(){

        return(
          <div>

            <Card className="cardAbout" >
                <CardActionArea  onClick={this.handleOpen} > 
                  <CardMedia
                    className="mediaAbout"
                    image={AboutPic}
                    title="Contemplative Reptile"
                  />
                  <CardContent style={{marginTop: '-0%'}}>
                    <Typography gutterBottom variant="h4" component="h2" style={{fontFamily: 'montserrat', fontWeight: '600', letterSpacing: '-0.05em', marginTop:"-80%"}}>
                    Hello,
                  </Typography>
                    <Typography variant="h5" color="textSecondary" component="p" className="subTitle" style={{fontFamily: 'montserrat', letterSpacing: '-0.02em', maxWidth: "60%", marginTop: "-3%"}}>
                      My name is Albert Collell
                    </Typography>

                  </CardContent>
                </CardActionArea>
              </Card> 

              <Modal
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
              open={this.state.open}
              onClose={this.handleClose}
              >
            <div className="modalStyleAbout">
                 <img src={AboutPic} alt="albertcollell" className="imageStyleAbout"></img>
                <Clear className="mdc-icon-button material-icons" onClick={this.handleClose} style={{position: 'absolute', top: '10px', right: '10px', size: '36dp'}}>CLOSE</Clear>

                <div style= {{padding: '2% 10% 10% 10%', fontFamily: 'montserrat'}}>
                <Typography gutterBottom variant="h4" component="h2" style={{fontFamily: 'montserrat', fontWeight: '600', letterSpacing: '-0.05em', marginTop:"-80%"}}>
                    Hello,
                  </Typography>
                    <Typography variant="h5" color="textSecondary" component="p" className="subTitle" style={{fontFamily: 'montserrat', letterSpacing: '-0.02em', maxWidth: "60%", marginTop: "-3%"}}>
                      My name is Albert Collell
                    </Typography>

                  <div style= {{letterSpacing: '-0.02em', fontWeight: '300', paddingBottom: '10%'}}>
                    <p id="simple-modal-description"> 
                    Visionary leader with broad range of skills and experience, with the ability to build and manage multi-cultural technical and operations teams. Strong technical background and experienced in leading projects from concept to end product. International experience and adept at working and contributing in corporate as well as entrepreneurial environments. 
                    </p>
                    <Typography variant="h5" color="textSecondary" component="p" className="subTitle" style={{fontFamily: 'montserrat', letterSpacing: '-0.02em'}}>
                    More about me:                   
                    </Typography>


                </div>

              </div> 
              </div> 
            </Modal>







        </div>

        );
      }
      }
