import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import oneYearNomad from '../../Assets/oneyearnomad.jpg';
import oneyearnomadMobile from '../../Assets/oneyearnomadMobile.png';
import Modal from '@material-ui/core/Modal';
import Clear from '@material-ui/icons/Clear';
import CardsData from '../data/CardsData.json';
import './Test.css';




export default class MediaCards extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      idmodal: 0
    };
    this.handleOpen = this.handleOpen.bind(this);
    this.handleClose = this.handleClose.bind(this);


  }

  handleOpen(e){
    this.setState(({
      open: true,
      idmodal: e
    }));
    console.log(this.state.idmodal);
  }

  handleClose(){
    this.setState(({
      open: false
    }));
  }

  render(){

    return(
      <div>
      {CardsData.map((cardDetail, index)=> {
        return( 
        <Card className="card">
            <CardActionArea  onClick={(e) => this.handleOpen(cardDetail.id)}>
              <CardMedia
                className="media"
                image={oneYearNomad}
                title="Contemplative Reptile"
              />
              <CardContent>
              <Typography variant="body2" color="textSecondary" component="p" className="subTitle" style={{fontFamily: 'montserrat', letterSpacing: '-0.02em'}}>
                  {cardDetail.subtitle}
                </Typography>
                <Typography gutterBottom variant="h5" component="h2" style={{fontFamily: 'montserrat', fontWeight: '600', letterSpacing: '-0.05em'}}>
                {cardDetail.title}
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card> 




            )       
        }   
    )
    }

    <div>
            <Modal
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
              open={this.state.open}
              onClose={this.handleClose}
              >
              <div className="modalStyle">
              <img src={oneYearNomad} alt="oneyearnomad" className="imageStyle"></img>

                <Clear className="mdc-icon-button material-icons" onClick={this.handleClose} style={{position: 'absolute', top: '10px', right: '10px', size: '36dp'}}>CLOSE</Clear>

                <div style= {{padding: '2% 10% 10% 10%', fontFamily: 'montserrat'}}>

                  <p style={{fontStyle: 'italic', fontSize: '14px'}}>{CardsData[this.state.idmodal].subtitle}</p>
                  <h1 id="modal-title" style={{fontFamily: 'montserrat', fontWeight: '600', fontSize: '24px', letterSpacing: '-0.05em'}
}>{CardsData[this.state.idmodal].title}</h1>
                  <Button variant="contained" color="primary"> VISIT </Button>
                  <div style= {{letterSpacing: '-0.02em', fontWeight: '400', paddingBottom: '10%'}}>
                    <p id="simple-modal-description"> 
                    DESCRIPTION
                  </p>
                  <h3>
                  FIRST LIST
                  </h3>
                  <li>Web development.</li>
                  <li>Social Media strategy.</li>
                  <li>Front End Design</li>
                  <h3 >
                  SECOND LIST
                  </h3>
                  <li>Wordpress</li>
                  <li>Elementor</li>
                  <li>Adobe Creative Suite</li>
                  </div>
                  <br></br>

                  <img src={oneyearnomadMobile} alt="oneyearnomadMobile" style={{maxWidth: '100%', maxHeight: '100%', borderRadius: '10px', boxShadow: '0 2px 2px 0 rgba(0,0,0,0.20)'}}></img>
                </div> 
              </div> 
            </Modal>

            </div>  

    </div>

    );
  }
  }
